var startButton = document.getElementById("start-btn");
var nextButton = document.getElementById("next-btn");
var questionContainerElement = document.getElementById("question-container");
var questionElement = document.getElementById("question");
var answerButtonsElement = document.getElementById("answer-buttons");
var resultForm = document.getElementById("form-result");
var countRightAnswers = 0;
var shuffledQuestions, currentQuestionIndex;
var currentQuestion = 1;
startButton.addEventListener("click", startGame);
nextButton.addEventListener("click", function () {
  document.getElementById("answer-buttons").classList.remove("no-click");
  currentQuestionIndex++;
  setNextQuestion();
  currentQuestion++;
  document.getElementById("current-question").innerHTML = currentQuestion;
});

function startGame() {
  console.log("started");
  document.getElementById("answer-buttons").classList.remove("no-click");
  startButton.classList.add("hide");
  resultForm.classList.add("hide");
  shuffledQuestions = questions.sort(function () {
    return Math.random() - 0.5;
  });
  currentQuestionIndex = 0;
  questionContainerElement.classList.remove("hide");
  setNextQuestion();
  currentQuestion = 1;
  document.getElementById("current-question").innerHTML = currentQuestion;
  countRightAnswers = 0;
  document.getElementById("all-questions2").innerHTML = questions.length;
  document.getElementById("all-questions").innerHTML = questions.length;
}

function setNextQuestion() {
  resetState();
  showQuestion(shuffledQuestions[currentQuestionIndex]);
}

function showQuestion(question) {
  questionElement.innerText = question.question;
  question.answers.forEach(function (answer) {
    var button = document.createElement("button");
    button.innerText = answer.text;
    button.classList.add("btn");

    if (answer.correct) {
      button.dataset.correct = answer.correct;
    }

    button.addEventListener("click", selectAnswer);
    answerButtonsElement.appendChild(button);
  });
}

function resetState() {
  nextButton.classList.add("hide");

  while (answerButtonsElement.firstChild) {
    answerButtonsElement.removeChild(answerButtonsElement.firstChild);
  }
}

function selectAnswer(e) {
  var selectedButton = e.target;
  var correct = selectedButton.dataset.correct;
  Array.from(answerButtonsElement.children).forEach(function (button) {
    setStatusClass(button, button.dataset.correct);
  });

  if (shuffledQuestions.length > currentQuestionIndex + 1) {
    nextButton.classList.remove("hide");
  } else {
    resultForm.classList.remove("hide");
    questionContainerElement.classList.add("hide");
    startButton.innerText = "Repeat";
    startButton.classList.remove("hide");
  }

  if (selectedButton.dataset = correct) {
    countRightAnswers++; //+1
  }

  document.getElementById("right-answers").innerHTML = countRightAnswers;
  document.getElementById("answers-percent").innerHTML = (100 * countRightAnswers / questions.length).toFixed(0);
  document.getElementById("answer-buttons").classList.add("no-click");
}

function setStatusClass(element, correct) {
  clearStatusClass(element);

  if (correct) {
    element.classList.add("correct");
  } else {
    element.classList.add("wrong");
  }
}

function clearStatusClass(element) {
  element.classList.remove("correct");
  element.classList.remove("wrong");
}

